import "./main.css";
export default function Main() {
  return (
    <main>
      <h1>Fun facts about React</h1>
    <ul>
      <li>To achieve a flexible dark and light theme</li>
      <li>The contrast difference between two colors</li>
      <li>This system allows flawless theme changes by switching</li>
      <li>Contrast difference is reserved for primary background and content.</li>
      <li>Pair and preseving their contrast and visual hierarchy.</li>
    </ul>
    </main>
  );
};
